mapper.insert:
insert into t_feed set
iuser= #{iuser}, contents= #{contents},
location= #{location}

mapper.select:
select a.ifeed, a.contents, a.location, a.created_at as createdAt, a.iuser as writerIuser, b.nm as writerNm, b.pic as writerPic, if(c.iuser is null, 0, 1) as isFav from t_feed a join t_user b on a.iuser= b.iuser
inner join t_feed_fav c
left join t_feed_fav c
on a.ifeed= c.ifeed and c.iuser= #{loginedIuser}
where a.iuser= #{targetIuser}
order by a.ifeed desc limit #{startIdx}, #{rowCount}
